import React from "react";
import { Link } from "react-router-dom";
import Homepage from ".";
import firebase from '../firebase';

class Signin extends React.Component {
  constructor(props) {
    super();
    this.state = { user: "", pass: "" , isOk : false};

    this.handleChangeUser = this.handleChangeUser.bind(this);
    this.handleChangePass = this.handleChangePass.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
   

  }
  componentDidMount(){
  
  }

  async login() {
    // const db = firebase.firestore();
    // var data = await db.collection("account").get();
    // console.log(data);

    const auth = firebase.auth()
    auth.signInWithEmailAndPassword("test@gmail.com","123456").then((result)=> {
      this.setState
    });
  }



  handleChangeUser(event) {
    this.setState({ user: event.target.value });
  }
  handleChangePass(event) {
    this.setState({ pass: event.target.value });
  }

  handleSubmit(event) {
    console.log(this.state);
  
    // this.setState({isOk: true});
    event.preventDefault();
  }

  render() {
    return (
    
      this.state.isOk ? Homepage()
      :
      (<div className="authincation section-padding">
        <div className="container h-100">
          <div className="row justify-content-center h-100 align-items-center">
            <div className="col-xl-5 col-md-6">
              <div className="mini-logo text-center my-5">
                <Link to={"./"}>
                  <img src={require("../../images/logo.png")} alt="" />
                </Link>
              </div>
              <div className="auth-form card">
                <div className="card-header justify-content-center">
                  <h4 className="card-title">Sign in</h4>
                </div>
                <div className="card-body">
                  <form method="post" name="myform" class="signin_validate">
                    <div className="form-group">
                      <label>Email</label>
                      <input
                        type="email"
                        className="form-control"
                        placeholder="hello@example.com"
                        name="email"
                        onChange={this.handleChangeUser}
                      />
                    </div>
                    <div className="form-group">
                      <label>Password</label>
                      <input
                        type="password"
                        className="form-control"
                        placeholder="Password"
                        name="password"
                        onChange={this.handleChangePass}
                      />
                    </div>
                    <div className="form-row d-flex justify-content-between mt-4 mb-2">
                      <div className="form-group mb-0">
                        <label className="toggle">
                          <input className="toggle-checkbox" type="checkbox" />
                          <span className="toggle-switch"></span>
                          <span className="toggle-label">Remember me</span>
                        </label>
                      </div>
                      <div class="form-group mb-0">
                        <Link to={"./reset"}>Forgot Password?</Link>
                      </div>
                    </div>
                    <div class="text-center">   
                      <button className="btn btn-success btn-block" onClick={this.handleSubmit}>
                        Sign in
                      </button>
                      {/* <Link to={"./"} className="btn btn-success btn-block">
                        Sign in
                      </Link> */}
                    </div>
                  </form>
                  <div className="new-account mt-3">
                    <p>
                      Don't have an account?{" "}
                      <Link className="text-primary" to={"./signup"}>
                        Sign up
                      </Link>
                    </p>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>)
    );
  }
}

